template.value:
String filter = request.getFilter();
Integer page = request.getPage();
Integer pageSize = request.getPageSize();
//排序
String sorted = request.getSorted();
if (StringUtils.isBlank(sorted)) {
    sorted = "$id$ desc";
}
org.springframework.data.domain.Page<$entity$> result = receiptBillDetailService.findAllWithPage(filter, page, pageSize, sorted);
List<$response$> detailList = Json.parseArray(Json.toJson(result.getContent()), $response$.class);
Page<$response$> pageVo = new Page<>(page, pageSize);
pageVo.setRecords(detailList);
pageVo.setTotalCount(result.getTotalElements());
return RestResponse.success(pageVo);
